import React,{Component, useCallback} from 'react';
var restoidurl="http://localhost:8700/restaurantdetail?_id=";
class order extends Component



{
    constructor(){

        super();
        this.state={
          "Order_id":Math.floor(Math.random()*100000),
            "name":"",
            "phone":"" ,
            restoname:''
        }
    }
    renderrest90=(data)=>{

        if(data)
        {
        return data.map((item)=>{
            return(
                <div className="form-group">
                <label>Resto Name:</label>
                <input type="text" className="form-control" value={item.name} readOnly/>
                        </div>
            )
        })
    }


  }
    render(){
        return(
            <div className="container">
                <div className="panel panel-success">

                    <div className="panel-heading">
                      <h3>Place Order</h3>
                    </div>
                    <div className="panel-body">
                        <div className="form-group">
                            <label>Order Id:</label>
<input type="text" className="form-control" value={this.state.Order_id} readOnly/>
                        </div>

                      
                           
     
   
                            {this.renderrest90(this.state.restoname)}
 
                            <div className="form-group">
                            <label>Name:</label>
<input type="text"  name="name" value={this.state.name} className="form-control" />
                        </div>




                            

                    </div>
                </div>
            </div>
        )
    }

    componentDidMount()
    {
        
       // fetch(locurl,{method:'GET'})
       // .then((res)=> res.json)
       // .then((data)=>{
            console.log("hello")
           // console.log(data);
      //  })
     
      fetch(`${restoidurl}${this.props.match.params.id}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
   this.setState({restoname:data});
      
    });
});
    }
}
export default order;
