import React,{Component} from 'react'
import order12 from './order12.css'
var orderUrl="http://localhost:8700/placeorder"

class OrderDisplay extends Component {
    constructor(){
      
      
        var mydate = new Date()
     
        super();
this.state={
    orders:""
}
    }
    orderdetail=(data)=>{
        if(data){
            return data.map((item)=>{
                return(
                    <div >
                    
        <div className="row">
            <div className="second1 col-md-6 col-sm-12">



          


                <img src="https://b.zmtcdn.com/data/res_imagery/307374_RESTAURANT_6688d81a57b8da4bcf20d725de39a3d2.jpg" height="130px" width="130px" id="logo1" height="130px" width="130px" id="logo1"  />
                <div className="tileComponent2">
                <div className="componentHeading1">
                 
                </div>
                <div></div>
                <div className="componentSubHeading1">
                    Order Id:{item._id}
                
                    
                </div>
               
               
                         
               
                 
            </div>
            <div id="nam">
                <b>Name</b>:  {item.name}
                </div>
                <div id="Address">
                    <b>Address</b>: {item.address}
                </div>
                <div id="Phone">
                    <b>Phone No</b>: {item.phone}
                   </div>
                   <div id="email">
                    <b> E-mail</b>: {item.email}
                   </div>
                   <p id="demo" class="dat"></p>
                   <script>

                   <div>
                   <p> mydate</p>
                   </div>
                   </script> 
                   
                </div>

               

                </div>
                    </div>
                )
            })
        }
    }

 
render(){
   
    return(
        <div>
<h1>Your Orders</h1>


<p id="demo"></p>



        {this.orderdetail(this.state.orders)}
      
        </div>
    )
}
componentDidMount()
    {
        
       // fetch(locurl,{method:'GET'})
       // .then((res)=> res.json)
       // .then((data)=>{
          //  console.log("hello")
           // console.log(data);
      //  })
     
      fetch('http://localhost:8700/allorders')
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
   this.setState({orders:data});
      
    });
});
    }
    

}
export default OrderDisplay
