import React,{Component} from 'react';
import second from './second.css'
import { Link } from 'react-router-dom';

const mealurl="http://localhost:8700/restaurantslist"
class Detail2 extends Component{
    constructor(props){

        super(props);
        this.state={
          rest90:'',
          cuisine:''
        }
    }
    renderrest90=(data)=>{

      if(data)
      {
      return data.map((item)=>{
          return(
            <Link to={`/detail3/${item._id}`}>
            <div className="second col-md-6 col-sm-12">




            <img src={item.thumb} height="130px" width="130px" id="logo1" height="130px" width="130px" id="logo1"  />
            <div className="tileComponent2">
            <div className="componentHeading1">
              {item.name}
            </div>
            <div className="componentSubHeading1">
          {item.locality}
            
                <hr/>
            </div>
           
           
                     
           
             
        </div>
        <div id="cui01">
           Cuisine:   {item.Cuisine[0].name},{item.Cuisine[1].name}
            </div>
            <div id="costing">
             Cost: {item.cost}
            </div>
        </div>
        </Link>
          )
        })
      }


    }
    sortfilter=(event)=>{
      console.log(event.target.value);
      let sortid=Number(event.target.value);
      sort=sortid;

      fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
        this.setState({rest90:data});
        
      
    });
 });

    }
    cuisinefilter=(event)=>{
  
     let cuisineid=Number(event.target.value);
     if(cuisineid!=6)
     {

   
     fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}?cuisine=${cuisineid}`)
     .then((response)=>{
      response.json()
     .then((data) => {
       console.log(data);
       this.setState({rest90:data});
       
     
   });
});
     }
     else{
      fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
        this.setState({rest90:data});
        
      
    });
 });
     }
    }
    costfilter=(event)=>{
      console.log(event.target.value);
     var cost=(event.target.value).split(',');
     var lcost=cost[0];
     var hcost=cost[1];
