import React,{Component} from 'react';
import Quicksearh from './Quicksearch'
import QuickSearchDisplay from './QuickSearchDisplay.css'
//import Quicksearch from '.component/Quicksearch'
//const locurl= "http://localhost:8700/city";
const restourl="http://localhost:8700/cityrestanrants/"
var myIndex = 0;
class Detail1 extends Component



{
    constructor(){

        super();
        this.state={
            title:'Edureka Internship',
            keyword:'User Text',
            city:'',
            location:'',
            restaurants:''
        }
    }
    handlechange=(event)=>{
        this.setState({keyword:event.target.value?event.target.value:'User Text'})

    }
    handlecity=(event)=>{
        this.setState({city:event.target.value})
        var cityId=parseInt(event.target.value)
        sessionStorage.setItem('cityId',cityId)
        //var cityid=parseInt(localStorage.setItem('cityId',event.target.value))?parseInt(localStorage.setItem('cityId')):1
        fetch(`${restourl}${cityId}`)
        .then((response)=>{
         response.json()
        .then((data) => {
    
  
         this.setState({restaurants:data});
      });
    });

    }
    renderResto =(data)=>{
        if(data)     
        {
                return  data.map((item)=>{
   
                return(

                          <option>
                               {item.name} | {item.locality}
                          </option>
        )
    })

}

}
    
    renderCity = (data1) => {

           if(data1)     
            {
                    return  data1.map((item)=>{
       
                    return(

                              <option value={item._id}>
                                   {item.name}
                              </option>
            )
        })
    
    }
   

}
carousel=()=> {
    var i;
    var x = document.getElementsByClassName("mySlides");
    for (i = 0; i < x.length; i++) {
      x[i].style.display = "none";  
    }
    myIndex++;
    if (myIndex > x.length) {myIndex = 1}    
    x[myIndex-1].style.display = "block";  
    setTimeout(carousel, 2000); // Change image every 2 seconds
  }
  
    render()
    {
        return(

           
            



           


 <div>
                <div >

                <img class="imageContainer"  style="width:100%"/>

                <img class="imageContainer1"  style="width:100%"/>

                <img class="imageContainer2"  style="width:100%"/>


var myIndex = 0;
carousel();





         <div id="log">
         <b id="log1">P!</b>
         </div>
           
        
         <div id="Title">
           
            <p >Find The Best Restaurants,Bars And Cafe's</p>
         </div>
         <div id="search">
         <select   id="input1" onChange={this.handlecity }>



              {this.renderCity(this.state.location)}
        </select>
                     
                     <datalist id="restaurant">
                 {this.renderResto(this.state.restaurants)}
              </datalist>
       
        <input list="restaurant" name="rests" id="input2"  placeholder="  Search for Restaurants"/>
      
       </div>  
        
      </div>
               <center>
                

         
                
               
&nbsp;
<div>
    <Quicksearh/> 
</div>
            
             
              </center>
              <br/>

           
            </div>   
            
        
           
        )
        
        
                
        }
    
  
    componentDidMount()
    {
        
       // fetch(locurl,{method:'GET'})
       // .then((res)=> res.json)
       // .then((data)=>{
          //  console.log("hello")
           // console.log(data);
      //  })
      fetch('http://localhost:8700/city')
      .then((response)=>{
       response.json()
      .then((data) => {
     //   console.log(data);

       this.setState({location:data});
    });
});
//.catch(function(error) {
   // console.log('Fetch Error:', error);
//});

    }
}
export default Detail1;
