import React,{Component} from 'react';
import order12 from './order12.css'
var restoidurl="http://localhost:8700/restaurantdetail?_id=";
var orderUrl="http://localhost:8700/placeorder"
class order extends Component{
    constructor(){
        super()
        
        

        this.state={
            Order_id : Math.floor(Math.random()*10000),
            date: new Date().toLocaleString(),
            name : "",
            phone : "",
            email : "",
            address : "",
            resto :"",
            restoname1:"",
            person : "",
           

        }
    }

    renderrest90=(data)=>{

        if(data)
        {
        return data.map((item)=>{
            return(
                <div className="form-group">
                
                <label className="control-label">Resto Name:</label>
                <input type="text" id="rname"  className="form-control" onChange={this.handleChangeRestoName} value={item.name}       />
              
                        </div>
                        
                         
            )
        })
    }


  }
  handleChangeName=(event) => {
    this.setState({name:event.target.value})
}
handleChangeRestoName=(event) => {
    var x = document.getElementById("rname").value;
  console.log(x);
    this.setState({resto:x})
}
handleChangePhone=(event) => {
    console.log("rset");
    this.setState({phone:event.target.value})
}
handleChangeEmail=(event) => {
    this.setState({email:event.target.value})
}
handleChangeAddress=(event) => {
    this.setState({address:event.target.value})
}
handleChangePerson=(event) => {
    this.setState({person:event.target.value})
}

handleSubmit=() => {
    var data = {
        "order_id":this.state.Order_id,
        "date":this.state.date,
        "name":this.state.name,
        "phone":this.state.phone,
        "email":this.state.email,
        "address":this.state.address,
        "person":this.state.person,
        "resto":this.state.resto,
        
        
    }
    fetch(orderUrl,{
        method:'POST',
        headers:{
            'Accept':'application/json',
            'Content-Type':'application/json'
        },
        body:JSON.stringify(data)
    })
    .then((this.props.history.push('/OrderDisplay')))
    console.log(data);
}
    render(){
        return(
            
            <div className="container"  >
                <div className="panel panel-success">
                    <div className="panel-heading">
                        <h3>Place Order</h3>
                    </div>
                    <div className="panel-body">
                    <div className="form-group">
                            <label>Order Id:</label>

                       <input type="text" name="Order_id" className="form-control"
                        value={this.state.Order_id} readOnly/>
                        </div>
                        {this.renderrest90(this.state.restoname)}
 
                        <div className="form-group">
                            <label className="control-label">Name:</label>
                            <input type="text" name="name" 
                            value={this.state.name}
                            className="form-control"
                            onChange={this.handleChangeName}/>
                        </div>
                        <div className="form-group">
                            <label className="control-label">Phone:</label>
                            <input type="text" name="phone" 
                            value={this.state.phone}
                            className="form-control"
                            onChange={this.handleChangePhone}/>
                        </div>
                        <div className="form-group">
                            <label className="control-label">Email:</label>
                            <input type="text" name="email" 
                            value={this.state.email}
                            className="form-control"
                            onChange={this.handleChangeEmail}/>
                        </div>
                        <div className="form-group">
                            <label className="control-label">address:</label>
                            <input type="text" name="address" 
                            value={this.state.address}
                            className="form-control"
                            onChange={this.handleChangeAddress}/>
                        </div>
                        <div className="form-group">
                            <label className="control-label">How many Person:</label>
                            <select name="person" value={this.state.person}
                            onChange={this.handleChangePerson}
                            className="form-control">
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                                <option value="4">4</option>
                                <option value="5">5</option>
                                <option value="6">6</option>
                            </select>
                        </div>
                        <div>
                            <button className="btn btn-success"
                            onClick={this.handleSubmit}
                            >Place Order</button>
                            &nbsp;&nbsp;&nbsp;
                            <button className="btn btn-danger"
                            onClick={this.handleCancel}>Cancel</button>
                        </div>
                        <div>
                      
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    componentDidMount()
    {
        
       // fetch(locurl,{method:'GET'})
       // .then((res)=> res.json)
       // .then((data)=>{
            console.log("hello")
           // console.log(data);
      //  })
     
  
     
      fetch(`${restoidurl}${this.props.match.params.id}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
   this.setState({restoname:data});
      
    });
});
    }
}


export default order;
