var orderUrl="http://localhost:8700/placeorder"
class OrderDisplay extends Component {
    constructor(){
        super();
this.state={
    orders:""
}
    }
    orderdetail=(data)=>{
        if(data){
            return data.map((item)=>{
                return(
                    <div>
                    <h3>{item.name}</h3>
                    <h2>{item.email}</h2>
                    </div>
                )
            })
        }
    }
render(){
    return(
        <div>
        {this.orderdetail(this.state.orders)}
        </div>
    )
}
