import React,{Component} from 'react';
import second from './second.css'
import { Link } from 'react-router-dom';

const mealurl="http://localhost:8700/restaurantslist"
class Detail2 extends Component{
    constructor(props){

        super(props);
        this.state={
          rest90:'',
          cuisine:''
        }
    }
    renderrest90=(data)=>{

      if(data)
      {
      return data.map((item)=>{
          return(
            <Link to={`/detail3/${item._id}`}>
            <div className="second col-md-6 col-sm-12">




            <img src={item.thumb} height="130px" width="130px" id="logo1" height="130px" width="130px" id="logo1"  />
            <div className="tileComponent2">
            <div className="componentHeading1">
              {item.name}
            </div>
            <div className="componentSubHeading1">
          {item.locality}
            
                <hr/>
            </div>
           
           
                     
           
             
        </div>
        <div id="cui01">
           Cuisine:   {item.Cuisine[0].name},{item.Cuisine[1].name}
            </div>
            <div id="costing">
             Cost: {item.cost}
            </div>
        </div>
        </Link>
          )
        })
      }


    }

    cuisinefilter=(event)=>{
     console.log(event.target.value);
     let cuisineid=Number(event.target.value);
     
     fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}?cuisine=${cuisineid}`)
     .then((response)=>{
      response.json()
     .then((data) => {
       console.log(data);
       this.setState({rest90:data});
     //  sessionStorage.setItem('mealId',this.props.match.params.id);
     
   });
});

    }
    render(){
   
        return(
<div>
          <nav className="navbar navbar-inverse ">
          <div className="container-fluid">
          <div className="navbar-header">
            <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar">
              <a className="icon-bar"></a>
   <a className="icon-bar"></a>
   <a className="icon-bar"></a> 
        
        </button>
        <a className="navbar-brand"  id="logo">P!</a>
        </div>
         <div className="collapse navbar-collapse" id="myNavbar">
                         <ul className="nav navbar-nav">
                             <li><a  id="a1">PatilResto</a></li>
                           
                         </ul>
                         <ul className="nav navbar-nav navbar-right">
                        <li><a href="#" id="ul2"><span className="glyphicon glyphicon-user" id="gf"></span> Create an account</a></li>
                        <li><a href="#" id="ul2"><span className="glyphicon glyphicon-log-in" id="gf"></span> LogIn</a></li>
                        </ul>
                       
                     </div>
       </div>
       
       
       
       </nav>
      
      <h2 id="titu">Search Restaurants In Mumbai</h2>
      <br/> 


      <div className="row">
      <div className="first col-md-6 col-sm-12">
         
        <div className="menu">
            <h3>Filters</h3>
           
            <h4>Select Option</h4>
             <select id="input1">
                      <option>Delhi</option>
                      <option>Mumbai</option>
                      <option>Chandigarh</option>
                      <option>Pune</option>
                      <option>Ahmednagar</option>
                  </select>
                  <br/>
                  <br/>
                  <h4>Cuisine</h4>
                  <div id="chk" onChange={this.cuisinefilter}>
                      <input type="radio" value='1' name="ccus" className="largecheck" id="large"/>&nbsp; North Indian<br/>
                      <input type="radio" value='2' name="ccus"  className="largecheck"/>&nbsp; South Indian<br/>
                      <input type="radio" value='3' name="ccus" className="largecheck"/>&nbsp; Chinese<br/>
                      <input type="radio" value='4' name="ccus" className="largecheck"/>&nbsp; FastFood<br/>
                      <input type="radio" value='5' name="ccus" className="largecheck"/>&nbsp; StreetFood<br/>
                 </div>
                 <br/>
                 <h4>Cost For Two</h4>
                 <div id="chk">
                 <input type="radio" name="age" className="largecheck"/>&nbsp;Less than Rs.500 <br/>
                 <input type="radio" name="age" checked="" className="largecheck"/>&nbsp;  Rs.500 To Rs.1000<br/>
                 <input type="radio" name="age" className="largecheck"/>&nbsp; Rs.1000 To Rs.1500 <br/>
                 <input type="radio" name="age" className="largecheck"/>&nbsp; Rs.1500 To Rs.2000 <br/>
                  <input type="radio" name="age" className="largecheck"/>&nbsp; Rs.2000+<br/>
          
                 
                </div>
                  <br/>
                 <h4>Sort</h4>
                  <div id="chk">
                 <input type="radio" name="age" className="largecheck"/>&nbsp; Price Low To High<br/>
                 <input type="radio" name="age" checked="" className="largecheck"/>&nbsp; Price High To Low<br/>
                 
           

      
                                             </div>

           





                                           </div>
