import React,{Component} from 'react'
import order12 from './order12.css'
import { Link } from 'react-router-dom'
var orderUrl="http://localhost:8700/placeorder"


class OrderDisplay extends Component {
    constructor(){
        
       
       
     
        super();
this.state={
    orders:"",
    
}
    }
    
    orderdetail=(data)=>{
        if(data){
            return data.map((item)=>{
                return(
                    <div >
                    
        <div className="row">
            <div className="second1 col-md-6 col-sm-12">


     
          


                <img src="https://b.zmtcdn.com/data/res_imagery/307374_RESTAURANT_6688d81a57b8da4bcf20d725de39a3d2.jpg" height="130px" width="130px" id="logo1" height="130px" width="130px" id="logo1"  />
                <div className="tileComponent2">
                <div className="componentHeading1">
                 
                </div>
                <div></div>
                <div className="componentSubHeading1">
                    Restaurant Name:<span id="nameofresto">{item.resto}</span>
                
                    
                </div>
               
                <div className="componentSubHeading3">
                Order Id:{item._id}
                
                    
                </div>
               
                         
               
                 
            </div>
            <div id="nam">
                <b>Name</b>:  {item.name}
                </div>
                <div id="Address">
                    <b>Address</b>: {item.address}
                </div>
                <div id="Phone">
                    <b>Phone No</b>: {item.phone}
                   </div>
                   <div id="email">
                    <b> E-mail</b>: {item.email}
                   </div>
               
                

                   <div id="dato" >
                   
                 <b id="dato">Date: </b>{item.date}
                   </div>
            
                   
                </div>

               

                </div>
                    </div>
                )
            })
        }
    }

 
render(){
    
    return(
        <div>

<nav className="navbar navbar-inverse ">
          <div className="container-fluid">
          <div className="navbar-header">
            <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar">
              <a className="icon-bar"></a>
   <a className="icon-bar"></a>
   <a className="icon-bar"></a> 
        
        </button>
        <a className="navbar-brand"  id="logo">P</a>
        </div>
         <div className="collapse navbar-collapse" id="myNavbar">
         <Link to={`/`}>
                         <ul className="nav navbar-nav">
                       
                             <li><a  id="a1">   HOME</a></li>
                             
                         </ul>
                         </Link>
                         <ul className="nav navbar-nav navbar-right">
                        <li><a href="#" id="ul2"><span className="glyphicon glyphicon-user" id="gf"></span> Create an account</a></li>
                        <li><a href="#" id="ul2"><span className="glyphicon glyphicon-log-in" id="gf"></span> LogIn</a></li>
                        </ul>
                       
                     </div>
       </div>
       
       
       
       </nav>
<h1>Your Orders</h1>



        {this.orderdetail(this.state.orders)}
        
        </div>
    )
}
componentDidMount()
    {       
       
        
        
        
       // fetch(locurl,{method:'GET'})
       // .then((res)=> res.json)
       // .then((data)=>{
          //  console.log("hello")
           // console.log(data);
      //  })
     
      fetch('http://localhost:8700/allorders')
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
   this.setState({orders:data});
      
    });
});
    }
    

}
export default OrderDisplay
