import React,{Component} from 'react';
import second from './second.css'
import { Link } from 'react-router-dom';

const mealurl="http://localhost:8700/restaurantslist"
class Detail2 extends Component{
    constructor(props){

        super(props);
        this.state={
          rest90:'',
          cuisine:''
        }
    }
    renderrest90=(data)=>{

      if(data)
      {
      return data.map((item)=>{
          return(
            <Link to={`/detail3/${item._id}`}>
            <div className="second col-md-6 col-sm-12">




            <img src={item.thumb} height="130px" width="130px" id="logo1" height="130px" width="130px" id="logo1"  />
            <div className="tileComponent2">
            <div className="componentHeading1">
              {item.name}
            </div>
            <div className="componentSubHeading1">
          {item.locality}
            
                <hr/>
            </div>
           
           
                     
           
             
        </div>
        <div id="cui01">
           Cuisine:   {item.Cuisine[0].name},{item.Cuisine[1].name}
            </div>
            <div id="costing">
             Cost: {item.cost}
            </div>
        </div>
        </Link>
          )
        })
      }


    }
    sortfilter=(event)=>{
      //console.log(event.target.value);
      let sortid=Number(event.target.value);
      var sort=sortid;

      fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}/?sort=${sortid}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
        this.setState({rest90:data});
        
      
    });
 });

    }
    cuisinefilter=(event)=>{
  
     let cuisineid=Number(event.target.value);
    
     
     if(cuisineid!=6)
     {

   
     fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}?cuisine=${cuisineid}`)
     .then((response)=>{
      response.json()
     .then((data) => {
       console.log(data);
       this.setState({rest90:data});
       
     
   });
});
     }
     else{
      fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
        this.setState({rest90:data});
        
      
    });
 });
     }
    }
    costfilter=(event)=>{
      console.log(event.target.value);
     var cost=(event.target.value).split(',');
     var lcost=cost[0];
     var hcost=cost[1];
     //console.log(`${lcost}`);
    // console.log(`${hcost}`); 
    fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}?hcost=${hcost}&lcost=${lcost}`)
    .then((response)=>{
     response.json()
    .then((data) => {
      console.log(data);
      this.setState({rest90:data});
      
    
  });
});
 
 
     }
    render(){
   
        return(
<div>
          <nav className="navbar navbar-inverse ">
          <div className="container-fluid">
          <div className="navbar-header">
            <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar">
              <a className="icon-bar"></a>
   <a className="icon-bar"></a>
   <a className="icon-bar"></a> 
        
        </button>
        <a className="navbar-brand"  id="logo">P</a>
        </div>
         <div className="collapse navbar-collapse" id="myNavbar">
         <Link to={`/`}>
                         <ul className="nav navbar-nav">
                       
                             <li><a  id="a1">   HOME</a></li>
                             
                         </ul>
                         </Link>
                         <ul className="nav navbar-nav navbar-right">
                        <li><a href="#" id="ul2"><span className="glyphicon glyphicon-user" id="gf"></span> Create an account</a></li>
                        <li><a href="#" id="ul2"><span className="glyphicon glyphicon-log-in" id="gf"></span> LogIn</a></li>
                        </ul>
                       
                     </div>
       </div>
       
       
       
       </nav>
      
      <h2 id="titu">Search Restaurants In Mumbai</h2>
      <br/> 


      <div className="row">
      <div className="first col-md-6 col-sm-12">
         
        <div className="menu">
            <h3>Filters</h3>
           
            <h4>Select Option</h4>
             <select id="input1">
                      <option>Delhi</option>
                      <option>Mumbai</option>
                      <option>Chandigarh</option>
                      <option>Pune</option>
                      <option>Ahmednagar</option>
                  </select>
                  <br/>
                  <br/>
                  <h4>Cuisine</h4>
                  <div id="chk" onChange={this.cuisinefilter}>
                      <input type="radio" value='1' name="ccus" className="largecheck" id="large"/>&nbsp; North Indian<br/>
                      <input type="radio" value='2' name="ccus"  className="largecheck"/>&nbsp; South Indian<br/>
                      <input type="radio" value='3' name="ccus" className="largecheck"/>&nbsp; Chinese<br/>
                      <input type="radio" value='4' name="ccus" className="largecheck"/>&nbsp; FastFood<br/>
                      <input type="radio" value='5' name="ccus" className="largecheck"/>&nbsp; StreetFood<br/>
                      <input type="radio" value='6' name="ccus" className="largecheck"/>&nbsp; All<br/>
                 </div>
                 <br/>
                 <h4>Cost For Two</h4>
                 <div id="chk" onChange={this.costfilter}  >
                 <input type="radio"  value='0,200' name="cos" className="largecheck"/>&nbsp;Less Than Rs.200 <br/>
                 <input type="radio"  value='200,300' name="cos"  className="largecheck"/>&nbsp;  Rs.200 To Rs.300<br/>
                 <input type="radio"  value='300,400' name="cos" className="largecheck"/>&nbsp; Rs.300 To Rs.400 <br/>
                 <input type="radio"  value='400,600' name="cos" className="largecheck"/>&nbsp; Rs.400 To Rs.600 <br/>
                  <input type="radio"  value='600,5000' name="cos" className="largecheck"/>&nbsp; Rs.600+<br/>
          
                 
                </div>
                  <br/>
                 <h4>Sort</h4>
        
                  <div id="chk" onChange={this.sortfilter} >
                 <input type="radio" value='1' name="sor" className="largecheck"/>&nbsp; Price Low To High<br/>
                 <input type="radio" value='-1'  name="sor" className="largecheck"/>&nbsp; Price High To Low<br/>
                 
           

      
                                             </div>

      
        



                                           </div>
                                     </div>

                                
                                     {this.renderrest90(this.state.rest90)}


      
                     



    



</div>

</div>
        )
        
        }
    componentDidMount()
    {
        
       // fetch(locurl,{method:'GET'})
       // .then((res)=> res.json)
       // .then((data)=>{
          //  console.log("hello")
           // console.log(data);
      //  })
     
      fetch(`${mealurl}/${sessionStorage.getItem('cityId')}/${this.props.match.params.id}`)
      .then((response)=>{
       response.json()
      .then((data) => {
        console.log(data);
        this.setState({rest90:data});
        sessionStorage.setItem('mealId',this.props.match.params.id);
      
    });
});
    }
    componentDidUpdate(){

      
    }
}
export default Detail2
